import React, { Component } from "react";
import { Image, StyleSheet, TextInput, View } from "react-native";
import { Text } from "../../Themed";
import { IconButton } from "../../StyledIconButton";
import Context from "../../../context/context";

export default class Character extends Component {
  static contextType = Context;

  constructor(props) {
    super(props);
    this.state = {
      isEditable: false,
    };
    this.onEditCharacter = this.onEditCharacter.bind(this);
    this.onDeleteCharacter = this.onDeleteCharacter.bind(this);
  }

  onEditCharacter() {
    this.setState({ isEditable: true });
  }

  onDeleteCharacter() {
    const { character, refreshList } = this.props;
    this.context.deleteCharacter(refreshList, character._id);
  }

  render() {
    const { character } = this.props;
    const { isEditable } = this.state;
    const { body, image, title } = character;
    return (
      <View style={styles.container}>
        <Image
          style={styles.tinyLogo}
          source={{
            uri: image,
          }}
        />
        <View style={styles.textContainer}>
          <Text style={styles.textFields} lightColor="white" darkColor="white">
            {title}
          </Text>
          {isEditable && (
            <TextInput
              style={styles.input}

              placeholder="Buscar"
              defaultValue={body}
            />
          )}
          {!isEditable && (
            <View style={styles.buttonContainer}>
              <IconButton
                name="pencil"
                size={30}
                color="red"
                onPress={this.onEditCharacter}
              />
              <IconButton
                name="trash"
                size={30}
                color="red"
                onPress={this.onDeleteCharacter}
              />
            </View>
          )}
        </View>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  tinyLogo: {
    height: 400,
  },
  buttonContainer: {
    display: "flex",
    flexDirection: "row",
    flex: 1,
    justifyContent: "space-between",
  },
  container: { backgroundColor: "black", margin: 16, padding: 8, width: 300 },
  textContainer: { flexDirection: "column" },
  textFields: { height: 36 },
  input: { backgroundColor: 'white'},
});
